/*
Deployment script for vtnt_asset_Org
*/

GO
SET ANSI_NULLS, ANSI_PADDING, ANSI_WARNINGS, ARITHABORT, QUOTED_IDENTIFIER ON;

SET CONCAT_NULL_YIELDS_NULL, NUMERIC_ROUNDABORT OFF;


GO
:setvar DatabaseName "vtnt_asset_Org"
:setvar DefaultDataPath "C:\Program Files\Microsoft SQL Server\MSSQL10.MSSQLSERVER\MSSQL\DATA\"
:setvar DefaultLogPath "C:\Program Files\Microsoft SQL Server\MSSQL10.MSSQLSERVER\MSSQL\DATA\"

GO
:on error exit
GO
USE [master]
GO
USE [$(DatabaseName)]
GO
Print N'Disabling all DDL triggers...'
GO
DISABLE TRIGGER ALL ON DATABASE
GO
IF EXISTS (SELECT * FROM tempdb..sysobjects WHERE id=OBJECT_ID('tempdb..#tmpErrors')) DROP TABLE #tmpErrors
GO
CREATE TABLE #tmpErrors (Error int)
GO
SET XACT_ABORT ON
GO
SET TRANSACTION ISOLATION LEVEL READ COMMITTED
GO
BEGIN TRANSACTION
GO
PRINT N'Creating [VTNT.Database.Utility]...';


GO
CREATE ASSEMBLY [VTNT.Database.Utility]
    AUTHORIZATION [dbo]
    FROM 
    WITH PERMISSION_SET = SAFE;


GO
IF @@ERROR <> 0
   AND @@TRANCOUNT > 0
    BEGIN
        ROLLBACK;
    END

IF @@TRANCOUNT = 0
    BEGIN
        INSERT  INTO #tmpErrors (Error)
        VALUES                 (1);
        BEGIN TRANSACTION;
    END


GO
PRINT N'Adding files to assembly [VTNT.Database.Utility]';


GO
ALTER ASSEMBLY [VTNT.Database.Utility]
    DROP FILE ALL
    ADD FILE FROM  AS N'StringFunctions.cs',  AS N'Properties\AssemblyInfo.cs',  AS N'String\CStringConvert.cs',  AS N'bin\Debug\VTNT.Database.Utility.pdb';


GO
PRINT N'Creating [dbo].[ConvertUNICODEToHopToUNICODE]...';


GO
CREATE FUNCTION [dbo].[ConvertUNICODEToHopToUNICODE]
(@str NVARCHAR (4000))
RETURNS NVARCHAR (4000)
AS
 EXTERNAL NAME [VTNT.Database.Utility].[UserDefinedFunctions].[ConvertUNICODEToHopToUNICODE]


GO
IF @@ERROR <> 0
   AND @@TRANCOUNT > 0
    BEGIN
        ROLLBACK;
    END

IF @@TRANCOUNT = 0
    BEGIN
        INSERT  INTO #tmpErrors (Error)
        VALUES                 (1);
        BEGIN TRANSACTION;
    END


GO
PRINT N'Creating [VTNT.Database.Utility].[SqlAssemblyProjectRoot]...';


GO
EXECUTE sp_addextendedproperty @name = N'SqlAssemblyProjectRoot', @value = N'D:\DEVELOP\VTNT\VnAccounting\Source\VTNT.Database.Utility', @level0type = N'Assembly', @level0name = N'VTNT.Database.Utility';


GO
IF @@ERROR <> 0
   AND @@TRANCOUNT > 0
    BEGIN
        ROLLBACK;
    END

IF @@TRANCOUNT = 0
    BEGIN
        INSERT  INTO #tmpErrors (Error)
        VALUES                 (1);
        BEGIN TRANSACTION;
    END


GO
PRINT N'Creating [dbo].[ConvertUNICODEToHopToUNICODE].[SqlAssemblyFile]...';


GO
EXECUTE sp_addextendedproperty @name = N'SqlAssemblyFile', @value = N'StringFunctions.cs', @level0type = N'SCHEMA', @level0name = N'dbo', @level1type = N'FUNCTION', @level1name = N'ConvertUNICODEToHopToUNICODE';


GO
IF @@ERROR <> 0
   AND @@TRANCOUNT > 0
    BEGIN
        ROLLBACK;
    END

IF @@TRANCOUNT = 0
    BEGIN
        INSERT  INTO #tmpErrors (Error)
        VALUES                 (1);
        BEGIN TRANSACTION;
    END


GO
PRINT N'Creating [dbo].[ConvertUNICODEToHopToUNICODE].[SqlAssemblyFileLine]...';


GO
EXECUTE sp_addextendedproperty @name = N'SqlAssemblyFileLine', @value = N'12', @level0type = N'SCHEMA', @level0name = N'dbo', @level1type = N'FUNCTION', @level1name = N'ConvertUNICODEToHopToUNICODE';


GO
IF @@ERROR <> 0
   AND @@TRANCOUNT > 0
    BEGIN
        ROLLBACK;
    END

IF @@TRANCOUNT = 0
    BEGIN
        INSERT  INTO #tmpErrors (Error)
        VALUES                 (1);
        BEGIN TRANSACTION;
    END


GO
IF EXISTS (SELECT * FROM #tmpErrors) ROLLBACK TRANSACTION
GO
IF @@TRANCOUNT>0 BEGIN
PRINT N'The transacted portion of the database update succeeded.'
COMMIT TRANSACTION
END
ELSE PRINT N'The transacted portion of the database update failed.'
GO
DROP TABLE #tmpErrors
GO
Print N'Reenabling DDL triggers...'
GO
ENABLE TRIGGER [DBA_tg_DDL_TraceRenameSP] ON DATABASE
GO
ENABLE TRIGGER [DBA_tg_DDL_TraceCreateSP] ON DATABASE
GO
